/*
	----------------
	- Modifier attributes
	----------------

[data-focus-userlinks]
	"radius" user links will inherit the radius of the parent
	"border" adds a vertical border between each user link
	"border-left" adds a border to the left side of user links
	"border-right" adds a border to the right side of user links
	"sep" adds a separator between some links


[data-focus-post]
	"margin" Adds a space between posts
	"no-margin" Removes the space between posts

	[data-focus-post-header]
		"offset" Offsets the post header by overlaying borders
		"radius" Forces --post-header--border-radius on headers
		"border" Adds a border to the header (useful for dark headers)
		"border-v" Adds a vertical border to the header (useful for light headers)
		"no-mobile-badges" Removes the Group badge from headers on mobiles
		"inherit-rep" Inherits text color for reputation on mobile

	[data-focus-post-controls]
		"wide" Stretch the post controls across the author pane
		"buttons" converts comment controls into buttons
			"transparent" Removes background and border from post controls
		"text" converts comment controls into a text row

[data-focus-blocks]
	"contrast" Adds backgrounds to elements, useful if a background image is used
	"transparent" Removes backgrounds, borders and shadows from box elements
	"sectiontitle" Separate the sectiontitle from ipsBox on certain pages

[data-focus-ui]
	"no-picker-mobile" Removes background picker from tablets and mobiles
	"ipsComment_solved" Adds a green header to solved content
	"no-cTopicPostArea" Removes the background from the reply area
	"social-inherit" Changes the background of social icons to area_background_dark
	"subforum-grid" Aligns subforums into a grid
	"transparent-ficon" Removes background effects from forum icons
	"new-badge" Adds a NEW badge next to unread forum titles

*/


/*
	----------------
	- Variables
	----------------
*/

